use sahana;
CREATE TABLE Hospital (
    PatientID INT PRIMARY KEY,
    FirstName VARCHAR(50),
    LastName VARCHAR(50),
    Age INT,
    Gender VARCHAR(10),
    Address1 VARCHAR(100),
    Address2 VARCHAR(100),
    City VARCHAR(50),
    State VARCHAR(50),
    ZipCode VARCHAR(20),
    Phone VARCHAR(20),
    Email VARCHAR(100),
    GuardianFirstName VARCHAR(50),
    GuardianLastName VARCHAR(50),
    EmergencyContact VARCHAR(20),
    BloodGroup VARCHAR(10),
    AdmissionDate DATE,
    DischargeDate DATE,
    Diagnosis VARCHAR(100),
    DoctorName VARCHAR(100),
    WardNumber INT,
    BedNumber INT,
    Medication1 VARCHAR(100),
    Medication2 VARCHAR(100),
    Medication3 VARCHAR(100),
    Medication4 VARCHAR(100),
    Medication5 VARCHAR(100),
    SurgeryDate DATE,
    SurgeryType VARCHAR(100),
    LabTest1 VARCHAR(100),
    LabTest2 VARCHAR(100),
    LabTest3 VARCHAR(100),
    LabTest4 VARCHAR(100),
    LabTestResult1 VARCHAR(100),
    LabTestResult2 VARCHAR(100),
    LabTestResult3 VARCHAR(100),
    LabTestResult4 VARCHAR(100),
    LabTestResult5 VARCHAR(100),
    BillingAmount DECIMAL(10, 2),
    PaymentStatus VARCHAR(20),
    InsuranceProvider VARCHAR(100),
    PolicyNumber VARCHAR(50),
    RoomType VARCHAR(50),
    RoomRate DECIMAL(8, 2),
    TotalStayDays INT,
    NextOfKinFirstName VARCHAR(50),
    NextOfKinLastName VARCHAR(50),
    NextOfKinPhone VARCHAR(20),
    NextOfKinEmail VARCHAR(100)
);

DESC Hospital;
   


